              var city = $("select[name=citie_id] :selected").val();                if(city != 0){                    $.post( "{{url('/regions_list')}}", { citie: city })                        .done(function( data ) {                            $('select[name=region_id]').html(data);                        });                }            }) ;        });        //	</script>	<script type="text/javascript" >        $(document).ready(function()        {            $('#clickmewow').click(function()            {                $('#radio1003').attr('checked', 'checked');            });        })	</script>@endsection