').html(data);                    });            }) ;        });        $(document).ready(function(){            var vals = $("select[name=countrie_id] :selected").val();            var user_id = parseInt("{{ $shiping->user_id }}");            if(vals != 0){                $.post( "{{url('get_user_shiping')}}", { countrie: vals  , user_id: user_id })                    .done(function( data ) {                        $('#shiping_data').html(data);                    });            }        });	</script>@stop