@if($messages->message->last())    @foreach ($messages->message as $message)        <div class="answer @if($message->user_from->id == Auth::user()->id) right @else left @endif">            <div class="avatar">                @if($message->user_from->attach_user_photo)                    <img style="border-radius: 50%; width: 40px; height: 40px;" src="{{ URL::to('public')}}{{$message->user_from->attach_user_photo}}" alt="">                @else                    <img style="border-radius: 50%; width: 40px; height: 40px;" src="{{ URL::to('public') }}{{theme_url('images/user_1523545943_19772.png')}}" alt="">                @endif                <div class="status offline"></div>            </div>            <div class="name">{{ $message->user_from->name }}</div>            <div class="text">                {{ $message->details  }}            </div>            <div class="time">{{ $message->created_at->diffForHumans()  }}</div>        </div>    @endforeach@endif